sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var ga = new GlideAjax('getCustomerInfo');
    ga.addParam('sysparm_name', 'getEngineOil');
    ga.addParam('sysparm_engine_oil', newValue);
    ga.getXML(HelloWorldParse);

    function HelloWorldParse(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
       // alert(answer);
        var valLable = g_form.getLabelOf("u_engine_oil");
        var conbn = valLable + " : " + answer;


            g_form.setValue("u_temp_billing", conbn);

    }

}
